# -*- coding:utf-8 -*-# Author: Zhu Chen # Organization: 07 LP detection group# Create Time: 2020/03  All rights reservedimport xadminfrom xadmin import viewsfrom .models import User, EmailVeriRecordclass UserAdmin(object):    list_display = ['username', 'email', 'password', 'create_time', 'is_active']    search_fields = ['username', 'email', 'password', 'is_active']    list_filter = ['username', 'email', 'password', 'create_time', 'is_active']# User继承了AbstractUser, 所以要先把xadmin注册的User去掉再重新注册xadmin.site.unregister(User)xadmin.site.register(User, UserAdmin)class EmailVeriRecordAdmin(object):    list_display = ['code', 'email', 'send_time', 'expire_time', 'email_type']    search_fields = ['code', 'email', 'send_time', 'expire', 'email_type']    list_filter = ['code', 'email', 'send_time', 'expire_time', 'email_type']xadmin.site.register(EmailVeriRecord, EmailVeriRecordAdmin)class BaseSetting(object):    '''    管理后台基础设置    '''    enable_themes = False    use_bootswatch = Falsexadmin.site.register(views.BaseAdminView, BaseSetting)class GlobalSettings(object):    '''    管理后台全局设置    '''    site_title = "吊销车牌特案组后台管理系统"    site_footer = "www.vmoli.com"    # menu_style = "accordion"xadmin.site.register(views.CommAdminView, GlobalSettings)