# -*- coding:utf-8 -*-# Author: Zhu Chen # Organization: 07 LP detection group# Create Time: 2020/04  All rights reservedfrom django.core.mail import send_mailfrom app1.models import EmailVeriRecordimport randomimport datetimefrom vmoli_com import settings# 随机产生验证码的函数def random_codechr(length=16):    # 随机大小写组合的验证码    chars = 'quFDGDbtwehykjahuhufHFCUHNCWEHAFDONCJUHU'    codechr = ''    for x in range(length):        # 随机取出一个字符        codechr += random.choice(chars)    return codechrdef send_email(to_email, email_type='register'):    """    :param to_email: 收件人的邮箱    :param email_type: 邮件类型 register注册或forget找回    :return: 邮件发送结果    """    email = EmailVeriRecord()    # 获取验证码    email.code = random_codechr()    # 收件人    email.email = to_email    # 过期时间    email.exprie_time = datetime.datetime.now() + datetime.timedelta(days=7)    # 邮件类型    email.email_type = email_type    # 发送邮件    try:        res = send_mail(            'Subject 激活邮件认证',            'Here is you code:{0}, please varify your account in 7 days'.format(email.code),            settings.EMAIL_HOST_USER,            [to_email], )        if res == 1:            # 保存邮件记录            email.save()            return True        else:            return False    except EmailVeriRecord as e:        print(e)        return False